mapper.update:
UPDATE TBL_RESERVATION
   SET START_TIME = #{ startTime },
       END_TIME = #{ endTime },
       RESERVATION_DATE = #{ reservationDate },
       DESCRIPTION = #{ description }
 WHERE RESERVATION_NO = #{ reservationNo }

mapper.delete:
DELETE FROM TBL_RESERVATION
 WHERE RESERVATION_NO IN

    #{ item }

mapper.select:
SELECT A.RESERVATION_NO, A.ASSET_CODE, B.ASSET_NAME, C.MEMBER_NAME, A.TEAM, A.DESCRIPTION, A.START_TIME, A.END_TIME, DATE_FORMAT(A.RESERVATION_DATE,'%Y-%m-%d') AS RESERVATION_DATE FROM TBL_RESERVATION A JOIN TBL_ASSET B ON A.ASSET_CODE = B.ASSET_CODE JOIN TBL_MEMBER C ON A.MEMBER_CODE = C.MEMBER_CODE WHERE A.ASSET_CODE = #{ assetCode }
AND (A.START_TIME >= #{ startDateTime } AND A.START_TIME <= #{ endDateTime } OR A.END_TIME >= #{ startDateTime } AND A.END_TIME <= #{ endDateTime }) OR (A.START_TIME < #{ startDateTime } AND A.END_TIME > #{ endDateTime })
ORDER BY (A.END_TIME - A.START_TIME) DESC
